import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [visible, setVisible] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const [showWelcomePopup, setShowWelcomePopup] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isMinimized, setIsMinimized] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowWelcomePopup(false);
    }, 5000);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (!visible && messages.length > 0) {
      const lastMessage = messages[messages.length - 1];
      if (lastMessage.from === 'bot') {
        setUnreadCount(prev => prev + 1);
      }
    }
  }, [messages, visible]);

  useEffect(() => {
    if (visible && messages.length === 0) {
      setTimeout(() => {
        setMessages(prev => [...prev, {
          from: 'bot',
          text: 'ðŸ‘‹ Hi there! I\'m your virtual assistant. How can I help you today?',
          timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
        setUnreadCount(0);
      }, 300);
    }
  }, [visible]);

  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      const fetchSuggestions = async () => {
        if (userInput.trim().length < 2) {
          setSuggestions([]);
          return;
        }

        try {
          const response = await axios.get(`http://localhost:8000/autocomplete?query=${userInput}`);
          const questions = response.data.map(item => item.question || item);
          setSuggestions(questions.slice(0, 5));
        } catch (error) {
          console.error('Autocomplete fetch failed', error);
          setSuggestions([]);
        }
      };

      fetchSuggestions();
    }, 300);

    return () => clearTimeout(debounceTimer);
  }, [userInput]);

  const sendMessage = async (text = userInput) => {
    if (text.trim() === '') return;

    const userMessage = {
      from: 'user',
      text,
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };
    setMessages(prev => [...prev, userMessage]);
    setSuggestions([]);
    setUserInput('');
    setIsTyping(true);

    try {
      const response = await axios.post('http://localhost:8000/botman', { message: text });

      setTimeout(() => {
        const botReply = response.data.messages?.[0]?.text || "ðŸ¤– Sorry, I didn't quite understand that. Could you rephrase or try asking differently?";
        setMessages(prev => [...prev, {
          from: 'bot',
          text: botReply,
          timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
        setIsTyping(false);
      }, 1000 + Math.random() * 1000);
    } catch (error) {
      setMessages(prev => [...prev, {
        from: 'bot',
        text: "âš ï¸ Our systems are currently unavailable. Please try again later or contact support@example.com",
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
      setIsTyping(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    sendMessage(suggestion);
  };

  const clearChat = () => {
    setMessages([]);
    setUserInput('');
  };

  const toggleChat = () => {
    setVisible(!visible);
    if (!visible) setUnreadCount(0);
  };

  const toggleMinimize = () => {
    setIsMinimized(!isMinimized);
  };

  const quickOptions = [
    'Reset Password',
    'Check Order Status',
    'Payment Options',
    'Contact Support',
    'Account Settings',
    'FAQ'
  ];

  return (
    <div className={`chatbot-wrapper ${visible ? 'visible' : ''}`}>
      {/* Chat UI goes here including the message list, input, buttons, and animations */}
      {/* Use the existing JSX structure and styling you had before */}
    </div>
  );
}

export default Chatbot;
