// src/ChatBotPage.js
import React, { useState } from 'react';
import axios from 'axios';

const ChatBotPage = () => {
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'üëã Hi! Ask me anything.' }
  ]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (!input.trim()) return;

    // Add user's message
    setMessages(prev => [...prev, { sender: 'user', text: input }]);

    try {
      const response = await axios.post('http://localhost:8000/botman', {
        message: input,
        driver: 'web',
        userId: '1234'
      });

      // Handle bot response (text or structured)
      const botReply = typeof response.data === 'string'
        ? response.data
        : JSON.stringify(response.data);

      setMessages(prev => [...prev, { sender: 'bot', text: botReply }]);
    } catch (err) {
      setMessages(prev => [...prev, { sender: 'bot', text: '‚ùå Error talking to bot.' }]);
    }

    setInput('');
  };

  return (
    <div style={styles.container}>
      <h2>üß† Customer Support Chatbot</h2>
      <div style={styles.chatBox}>
        {messages.map((msg, i) => (
          <div key={i} style={{ ...styles.message, textAlign: msg.sender === 'user' ? 'right' : 'left' }}>
            <strong>{msg.sender === 'user' ? 'You' : 'Bot'}:</strong> {msg.text}
          </div>
        ))}
      </div>
      <div style={styles.inputArea}>
        <input
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={e => setInput(e.target.value)}
          style={styles.input}
        />
        <button onClick={sendMessage} style={styles.button}>Send</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    width: '400px',
    margin: '50px auto',
    fontFamily: 'Arial, sans-serif'
  },
  chatBox: {
    border: '1px solid #ccc',
    borderRadius: '5px',
    padding: '10px',
    height: '300px',
    overflowY: 'auto',
    backgroundColor: '#f9f9f9'
  },
  message: {
    margin: '5px 0'
  },
  inputArea: {
    display: 'flex',
    marginTop: '10px'
  },
  input: {
    flex: 1,
    padding: '8px',
    fontSize: '1rem'
  },
  button: {
    padding: '8px 15px',
    marginLeft: '5px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px'
  }
};

export default ChatBotPage;
