import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [visible, setVisible] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const [showWelcomePopup, setShowWelcomePopup] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Hide welcome popup after a few seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowWelcomePopup(false);
    }, 5000);
    return () => clearTimeout(timer);
  }, []);

  // Show initial bot message when chat opens
  useEffect(() => {
    if (visible && messages.length === 0) {
      setTimeout(() => {
        setMessages(prev => [...prev, { 
          from: 'bot', 
          text: 'üëã Hi! How can I help you today?',
          timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
      }, 300);
    }
  }, [visible]);

  // Autocomplete suggestions with debounce
  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      const fetchSuggestions = async () => {
        if (userInput.trim().length < 2) {
          setSuggestions([]);
          return;
        }

        try {
          const response = await axios.get(`http://localhost:8000/autocomplete?query=${userInput}`);
          const questions = response.data.map(item => item.question || item);
          setSuggestions(questions.slice(0, 5)); // Limit to 5 suggestions
        } catch (error) {
          console.error('Autocomplete fetch failed', error);
          setSuggestions([]);
        }
      };

      fetchSuggestions();
    }, 300);

    return () => clearTimeout(debounceTimer);
  }, [userInput]);

  const sendMessage = async (text = userInput) => {
    if (text.trim() === '') return;

    const userMessage = { 
      from: 'user', 
      text,
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };
    setMessages(prev => [...prev, userMessage]);
    setSuggestions([]);
    setUserInput('');
    setIsTyping(true);

    try {
      const response = await axios.post('http://localhost:8000/botman', { message: text });
      
      // Simulate typing delay for more natural interaction
      setTimeout(() => {
        const botReply = response.data.messages?.[0]?.text || "ü§ñ Sorry, I didn't understand that. Could you rephrase?";
        setMessages(prev => [...prev, { 
          from: 'bot', 
          text: botReply,
          timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
        setIsTyping(false);
      }, 1000 + Math.random() * 1000); // Random delay between 1-2 seconds
    } catch (error) {
      setMessages(prev => [...prev, { 
        from: 'bot', 
        text: "‚ö†Ô∏è Sorry, I'm having trouble connecting. Please try again later.",
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
      setIsTyping(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    sendMessage(suggestion);
  };

  const clearChat = () => {
    setMessages([]);
    setUserInput('');
  };

  const quickOptions = ['Reset Password', 'Check Email Status', 'Track My Order', 'Payment Options', 'Contact Support'];

  return (
    <div className={`chatbot-wrapper ${visible ? 'visible' : ''}`}>
      <div className="chat-icon-container">
        <button className="chat-toggle" onClick={() => setVisible(!visible)}>
          {visible ? '‚úï' : 'üí¨ Chat'}
        </button>

        {showWelcomePopup && !visible && (
          <div className="welcome-popup">
            <span className="wave">üëã</span> Need help? Click here to chat!
          </div>
        )}
      </div>

      {visible && (
        <div className="chatbox">
          <div className="chat-header">
            <div className="header-content">
              <div className="bot-avatar">ü§ñ</div>
              <div>
                <h3>Virtual Assistant</h3>
                <p className="status">{isTyping ? 'Typing...' : 'Online'}</p>
              </div>
            </div>
            <button className="clear-btn" onClick={clearChat} title="Clear conversation">
              <span role="img" aria-label="clear">üóëÔ∏è</span>
            </button>
          </div>

          <div className="chat-messages">
            {messages.length === 0 && (
              <div className="empty-state">
                <p>How can I help you today?</p>
                <div className="quick-buttons">
                  {quickOptions.map((option, i) => (
                    <button key={i} onClick={() => sendMessage(option)}>{option}</button>
                  ))}
                </div>
              </div>
            )}

            {messages.map((msg, index) => (
              <div key={index} className={`message ${msg.from}`}>
                <div className="message-content">
                  {msg.from === 'bot' && <div className="avatar">ü§ñ</div>}
                  <div className="message-bubble">
                    <div className="message-text">{msg.text}</div>
                    <div className="message-time">{msg.timestamp}</div>
                  </div>
                  {msg.from === 'user' && <div className="avatar">üë§</div>}
                </div>
              </div>
            ))}

            {isTyping && (
              <div className="message bot">
                <div className="message-content">
                  <div className="avatar">ü§ñ</div>
                  <div className="message-bubble">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {suggestions.length > 0 && (
            <div className="suggestions-container">
              {suggestions.map((sug, index) => (
                <button 
                  key={index} 
                  className="suggestion-item"
                  onClick={() => handleSuggestionClick(sug)}
                >
                  {sug}
                </button>
              ))}
            </div>
          )}

          <div className="chat-input-container">
            <div className="chat-input">
              <input
                type="text"
                placeholder="Type your message..."
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                aria-label="Type your message"
              />
              <button 
                onClick={() => sendMessage()} 
                disabled={userInput.trim() === ''}
                aria-label="Send message"
              >
                <svg viewBox="0 0 24 24" width="24" height="24">
                  <path fill="currentColor" d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"></path>
                </svg>
              </button>
            </div>
            <div className="quick-buttons">
              {quickOptions.map((option, i) => (
                <button key={i} onClick={() => sendMessage(option)}>{option}</button>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chatbot;
