import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [visible, setVisible] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const [showWelcomePopup, setShowWelcomePopup] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isMinimized, setIsMinimized] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);
  const [correction, setCorrection] = useState(null);
  const messagesEndRef = useRef(null);

  // Auto-scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Hide welcome popup after a few seconds
  useEffect(() => {
    const timer = setTimeout(() => {
      setShowWelcomePopup(false);
    }, 5000);
    return () => clearTimeout(timer);
  }, []);

  // Track unread messages when chat is closed
  useEffect(() => {
    if (!visible && messages.length > 0) {
      const lastMessage = messages[messages.length - 1];
      if (lastMessage.from === 'bot') {
        setUnreadCount(prev => prev + 1);
      }
    }
  }, [messages, visible]);

  // Show initial bot message when chat opens
  useEffect(() => {
    if (visible && messages.length === 0) {
      setTimeout(() => {
        setMessages(prev => [...prev, { 
          from: 'bot', 
          text: '👋 Hi there! I\'m your virtual assistant. How can I help you today?',
          timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
        setUnreadCount(0);
      }, 300);
    }
  }, [visible]);

  // Enhanced autocomplete and autocorrect with debounce
  useEffect(() => {
    if (typingTimeout) {
      clearTimeout(typingTimeout);
    }

    const timer = setTimeout(() => {
      const fetchSuggestionsAndCorrections = async () => {
        if (userInput.trim().length < 2) {
          setSuggestions([]);
          setCorrection(null);
          return;
        }

        try {
          // Fetch both suggestions and corrections in parallel
          const [suggestionsResponse, correctionsResponse] = await Promise.all([
            axios.get(`http://localhost:8000/autocomplete?query=${userInput}`),
            axios.post(`http://localhost:8000/correct`, { text: userInput })
          ]);

          // Process suggestions
          const questions = suggestionsResponse.data.map(item => item.question || item);
          setSuggestions(questions.slice(0, 5));

          // Process corrections
          if (correctionsResponse.data?.suggestions?.length > 0) {
            const topCorrection = correctionsResponse.data.suggestions[0];
            if (topCorrection !== userInput.toLowerCase()) {
              setCorrection({
                original: userInput,
                corrected: topCorrection
              });
            } else {
              setCorrection(null);
            }
          } else {
            setCorrection(null);
          }
        } catch (error) {
          console.error('Fetch failed', error);
          setSuggestions([]);
          setCorrection(null);
        }
      };

      fetchSuggestionsAndCorrections();
    }, 300);

    setTypingTimeout(timer);

    return () => {
      if (typingTimeout) {
        clearTimeout(typingTimeout);
      }
    };
  }, [userInput]);

  const sendMessage = async (text = userInput) => {
    if (text.trim() === '') return;

    // Use corrected text if available and user hasn't modified it
    let finalText = text;
    if (correction && text === correction.original) {
      finalText = correction.corrected;
    }

    const userMessage = { 
      from: 'user', 
      text: finalText,
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };
    setMessages(prev => [...prev, userMessage]);
    setSuggestions([]);
    setCorrection(null);
    setUserInput('');
    setIsTyping(true);

    try {
      const response = await axios.post('http://localhost:8000/botman', { message: finalText });
      
      setTimeout(() => {
        const botReply = response.data.messages?.[0]?.text || "🤖 Sorry, I didn't quite understand that. Could you rephrase or try asking differently?";
        setMessages(prev => [...prev, { 
          from: 'bot', 
          text: botReply,
          timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }]);
        setIsTyping(false);
      }, 1000 + Math.random() * 1000);
    } catch (error) {
      setMessages(prev => [...prev, { 
        from: 'bot', 
        text: "⚠️ Our systems are currently unavailable. Please try again later or contact support@example.com",
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      }]);
      setIsTyping(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    sendMessage(suggestion);
  };

  const acceptCorrection = () => {
    if (correction) {
      setUserInput(correction.corrected);
      setCorrection(null);
    }
  };

  const clearChat = () => {
    setMessages([]);
    setUserInput('');
    setCorrection(null);
  };

  const toggleChat = () => {
    setVisible(!visible);
    if (!visible) setUnreadCount(0);
  };

  const toggleMinimize = () => {
    setIsMinimized(!isMinimized);
  };

  const quickOptions = [
    'Reset Password', 
    'Check Order Status', 
    'Payment Options', 
    'Contact Support',
    'Account Settings',
    'FAQ'
  ];

  return (
    <div className={`chatbot-wrapper ${visible ? 'visible' : ''}`}>
      <div className="chat-icon-container">
        <button className="chat-toggle" onClick={toggleChat}>
          <div className="chat-icon">
            <svg viewBox="0 0 24 24">
              <path fill="currentColor" d="M19.005 3.175H4.674C3.642 3.175 3 3.789 3 4.821V21.02l3.544-3.514h12.461c1.033 0 2.064-1.06 2.064-2.093V4.821c-.001-1.032-1.032-1.646-2.064-1.646zm-4.989 9.869H7.041V11.1h6.975v1.944zm3-4H7.041V7.1h9.975v1.944z"/>
            </svg>
          </div>
          {unreadCount > 0 && <span className="unread-badge">{unreadCount}</span>}
        </button>

        {showWelcomePopup && !visible && (
          <div className="welcome-popup">
            <span className="wave">👋</span> Need help? Click to chat with us!
          </div>
        )}
      </div>

      {visible && (
        <div className={`chatbox ${isMinimized ? 'minimized' : ''}`}>
          <div className="chat-header">
            <div className="header-content">
              <div className="bot-avatar">
                <svg viewBox="0 0 24 24">
                  <path fill="currentColor" d="M12,2A2,2 0 0,1 14,4C14,4.74 13.6,5.39 13,5.73V7H14A7,7 0 0,1 21,14H22A1,1 0 0,1 23,15V18A1,1 0 0,1 22,19H21V20A2,2 0 0,1 19,22H5A2,2 0 0,1 3,20V19H2A1,1 0 0,1 1,18V15A1,1 0 0,1 2,14H3A7,7 0 0,1 10,7H11V5.73C10.4,5.39 10,4.74 10,4A2,2 0 0,1 12,2M7.5,13A2.5,2.5 0 0,0 5,15.5A2.5,2.5 0 0,0 7.5,18A2.5,2.5 0 0,0 10,15.5A2.5,2.5 0 0,0 7.5,13M16.5,13A2.5,2.5 0 0,0 14,15.5A2.5,2.5 0 0,0 16.5,18A2.5,2.5 0 0,0 19,15.5A2.5,2.5 0 0,0 16.5,13Z"/>
                </svg>
              </div>
              <div>
                <h3>Virtual Assistant</h3>
                <p className="status">
                  {isTyping ? (
                    <span className="typing-status">
                      <span>Typing</span>
                      <span className="typing-dots">
                        <span>.</span>
                        <span>.</span>
                        <span>.</span>
                      </span>
                    </span>
                  ) : (
                    <span className="online-status">
                      <span className="online-dot"></span>
                      Online
                    </span>
                  )}
                </p>
              </div>
            </div>
            <div className="header-actions">
              <button className="minimize-btn" onClick={toggleMinimize} title={isMinimized ? 'Restore' : 'Minimize'}>
                {isMinimized ? (
                  <svg viewBox="0 0 24 24" width="16" height="16">
                    <path fill="currentColor" d="M19,13H5V11H19V13Z" />
                  </svg>
                ) : (
                  <svg viewBox="0 0 24 24" width="16" height="16">
                    <path fill="currentColor" d="M19,13H5V11H19V13Z" />
                  </svg>
                )}
              </button>
              <button className="clear-btn" onClick={clearChat} title="Clear conversation">
                <svg viewBox="0 0 24 24" width="16" height="16">
                  <path fill="currentColor" d="M19,4H15.5L14.5,3H9.5L8.5,4H5V6H19M6,19A2,2 0 0,0 8,21H16A2,2 0 0,0 18,19V7H6V19Z" />
                </svg>
              </button>
              <button className="close-btn" onClick={toggleChat} title="Close chat">
                <svg viewBox="0 0 24 24" width="16" height="16">
                  <path fill="currentColor" d="M19,6.41L17.59,5L12,10.59L6.41,5L5,6.41L10.59,12L5,17.59L6.41,19L12,13.41L17.59,19L19,17.59L13.41,12L19,6.41Z" />
                </svg>
              </button>
            </div>
          </div>

          {!isMinimized && (
            <>
              <div className="chat-messages">
                {messages.length === 0 && (
                  <div className="empty-state">
                    <div className="empty-icon">
                      <svg viewBox="0 0 24 24">
                        <path fill="currentColor" d="M12,3C6.5,3 2,6.58 2,11C2,13.13 3.05,15.07 4.75,16.5C4.7,17.1 4.33,18.67 2,21C4.37,20.89 6.64,20 8.47,18.5C9.61,18.83 10.81,19 12,19C17.5,19 22,15.42 22,11C22,6.58 17.5,3 12,3M12,17C7.58,17 4,14.31 4,11C4,7.69 7.58,5 12,5C16.42,5 20,7.69 20,11C20,14.31 16.42,17 12,17Z" />
                      </svg>
                    </div>
                    <p>How can I assist you today?</p>
                    <div className="quick-buttons">
                      {quickOptions.map((option, i) => (
                        <button key={i} onClick={() => sendMessage(option)}>{option}</button>
                      ))}
                    </div>
                  </div>
                )}

                {messages.map((msg, index) => (
                  <div key={index} className={`message ${msg.from}`}>
                    <div className="message-content">
                      {msg.from === 'bot' && (
                        <div className="avatar">
                          <svg viewBox="0 0 24 24">
                            <path fill="currentColor" d="M12,2A2,2 0 0,1 14,4C14,4.74 13.6,5.39 13,5.73V7H14A7,7 0 0,1 21,14H22A1,1 0 0,1 23,15V18A1,1 0 0,1 22,19H21V20A2,2 0 0,1 19,22H5A2,2 0 0,1 3,20V19H2A1,1 0 0,1 1,18V15A1,1 0 0,1 2,14H3A7,7 0 0,1 10,7H11V5.73C10.4,5.39 10,4.74 10,4A2,2 0 0,1 12,2M7.5,13A2.5,2.5 0 0,0 5,15.5A2.5,2.5 0 0,0 7.5,18A2.5,2.5 0 0,0 10,15.5A2.5,2.5 0 0,0 7.5,13M16.5,13A2.5,2.5 0 0,0 14,15.5A2.5,2.5 0 0,0 16.5,18A2.5,2.5 0 0,0 19,15.5A2.5,2.5 0 0,0 16.5,13Z" />
                          </svg>
                        </div>
                      )}
                      <div className="message-bubble">
                        <div className="message-text">{msg.text}</div>
                        <div className="message-time">{msg.timestamp}</div>
                      </div>
                      {msg.from === 'user' && (
                        <div className="avatar">
                          <svg viewBox="0 0 24 24">
                            <path fill="currentColor" d="M12,4A4,4 0 0,1 16,8A4,4 0 0,1 12,12A4,4 0 0,1 8,8A4,4 0 0,1 12,4M12,14C16.42,14 20,15.79 20,18V20H4V18C4,15.79 7.58,14 12,14Z" />
                          </svg>
                        </div>
                      )}
                    </div>
                  </div>
                ))}

                {isTyping && (
                  <div className="message bot">
                    <div className="message-content">
                      <div className="avatar">
                        <svg viewBox="0 0 24 24">
                          <path fill="currentColor" d="M12,2A2,2 0 0,1 14,4C14,4.74 13.6,5.39 13,5.73V7H14A7,7 0 0,1 21,14H22A1,1 0 0,1 23,15V18A1,1 0 0,1 22,19H21V20A2,2 0 0,1 19,22H5A2,2 0 0,1 3,20V19H2A1,1 0 0,1 1,18V15A1,1 0 0,1 2,14H3A7,7 0 0,1 10,7H11V5.73C10.4,5.39 10,4.74 10,4A2,2 0 0,1 12,2M7.5,13A2.5,2.5 0 0,0 5,15.5A2.5,2.5 0 0,0 7.5,18A2.5,2.5 0 0,0 10,15.5A2.5,2.5 0 0,0 7.5,13M16.5,13A2.5,2.5 0 0,0 14,15.5A2.5,2.5 0 0,0 16.5,18A2.5,2.5 0 0,0 19,15.5A2.5,2.5 0 0,0 16.5,13Z" />
                        </svg>
                      </div>
                      <div className="message-bubble">
                        <div className="typing-indicator">
                          <span></span>
                          <span></span>
                          <span></span>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
                <div ref={messagesEndRef} />
              </div>

              <div className="input-section">
                {correction && (
                  <div className="correction-banner">
                    <span>Did you mean: </span>
                    <button className="correction-text" onClick={acceptCorrection}>
                      {correction.corrected}
                    </button>
                    <button className="dismiss-correction" onClick={() => setCorrection(null)}>
                      ×
                    </button>
                  </div>
                )}

                {suggestions.length > 0 && (
                  <div className="suggestions-container">
                    {suggestions.map((sug, index) => (
                      <button 
                        key={index} 
                        className="suggestion-item"
                        onClick={() => handleSuggestionClick(sug)}
                      >
                        {sug}
                      </button>
                    ))}
                  </div>
                )}

                <div className="chat-input-container">
                  <div className="chat-input">
                    <input
                      type="text"
                      placeholder="Type your message..."
                      value={userInput}
                      onChange={(e) => setUserInput(e.target.value)}
                      onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                      aria-label="Type your message"
                    />
                    <button 
                      onClick={() => sendMessage()} 
                      disabled={userInput.trim() === ''}
                      aria-label="Send message"
                    >
                      <svg viewBox="0 0 24 24" width="24" height="24">
                        <path fill="currentColor" d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"></path>
                      </svg>
                    </button>
                  </div>
                  <div className="quick-buttons">
                    {quickOptions.slice(0, 4).map((option, i) => (
                      <button key={i} onClick={() => sendMessage(option)}>{option}</button>
                    ))}
                  </div>
                </div>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );

  
}

export default Chatbot;
