import React, { useState } from 'react';
import axios from 'axios';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');

  const sendMessage = async () => {
    if (userInput.trim() === '') return;

    const userMessage = { from: 'user', text: userInput };
    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await axios.post('http://localhost:8000/botman', {
        message: userInput
      });

      const botReply = response.data.messages?.[0]?.text || "Something went wrong.";
      const botMessage = { from: 'bot', text: botReply };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error(error);
      const errorMessage = { from: 'bot', text: "Oops! There was a problem. Try again." };
      setMessages(prev => [...prev, errorMessage]);
    }

    setUserInput('');
  };

  return (
    <div>
      <div style={{ border: '1px solid black', height: '300px', overflowY: 'auto', padding: '10px' }}>
        {messages.map((msg, index) => (
          <div key={index} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
            <p><strong>{msg.from}:</strong> {msg.text}</p>
          </div>
        ))}
      </div>
      <input
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        placeholder="Type your message"
        style={{ width: '80%' }}
      />
      <button onClick={sendMessage} style={{ width: '18%' }}>Send</button>
    </div>
  );
}

export default Chatbot;
