import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  return (
    <div className="app-container">
      <header className="header">
        <h1>Welcome to Our Virtual Assistant</h1>
        <p>Your smart companion for quick help and answers.</p>
      </header>

      <main className="main-content">
        <section>
          <h2>About</h2>
          <p>
            Our chatbot is designed to answer your questions instantly. Whether you need help with a service, want to
            know more about us, or just want to say hiâ€”our bot is here 24/7.
          </p>
        </section>

        <section>
          <h2>Features</h2>
          <ul>
            <li>Instant Q&A support</li>
            <li>Easy conversation flow</li>
            <li>Smart fallback responses</li>
          </ul>
        </section>
      </main>

      <Chatbot />
    </div>
  );
}

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    if (visible && messages.length === 0) {
      setTimeout(() => {
        setMessages(prev => [...prev, { from: 'bot', text: 'Hi, how can I help you today?' }]);
      }, 300);
    }
  }, [visible]);

  const sendMessage = async (text = userInput) => {
    if (text.trim() === '') return;

    const userMessage = { from: 'user', text };
    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await axios.post('http://localhost:8000/botman', {
        message: text
      });

      const botReply = response.data.messages?.[0]?.text || "Something went wrong.";
      const botMessage = { from: 'bot', text: botReply };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      const errorMessage = { from: 'bot', text: "Oops! There was a problem. Try again." };
      setMessages(prev => [...prev, errorMessage]);
    }

    setUserInput('');
  };

  const quickOptions = [
    'Reset Password',
    'Check Email',
    'Check Order',
    'Make Payment'
  ];

  return (
    <div className="chatbot-container">
      <button className="chat-toggle" onClick={() => setVisible(!visible)}>
        ðŸ’¬ Chat
      </button>

      {visible && (
        <div className="chatbox">
          <div className="chat-messages">
            {messages.map((msg, index) => (
              <div key={index} className={`message ${msg.from}`}>
                {msg.text}
              </div>
            ))}
          </div>

          <div className="quick-buttons">
            {quickOptions.map((option, i) => (
              <button key={i} onClick={() => sendMessage(option)}>{option}</button>
            ))}
          </div>

          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={() => sendMessage()}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
