import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [visible, setVisible] = useState(false);
  const [suggestions, setSuggestions] = useState([]);

  // Welcome message
  useEffect(() => {
    if (visible && messages.length === 0) {
      setTimeout(() => {
        setMessages(prev => [...prev, { from: 'bot', text: '👋 Hi! How can I help you today?' }]);
      }, 300);
    }
  }, [visible]);

  // Fetch autocomplete suggestions
  useEffect(() => {
    const fetchSuggestions = async () => {
      if (userInput.trim().length < 2) {
        setSuggestions([]);
        return;
      }

      try {
        const response = await axios.get(`http://localhost:8000/autocomplete?query=${userInput}`);
        const data = response.data;

        const questions = data.map(item => item.question || item);
        setSuggestions(questions);
      } catch (error) {
        console.error('Autocomplete fetch failed', error);
        setSuggestions([]);
      }
    };

    fetchSuggestions();
  }, [userInput]);

  // Send message to chatbot
  const sendMessage = async (text = userInput) => {
    if (text.trim() === '') return;

    const userMessage = { from: 'user', text };
    setMessages(prev => [...prev, userMessage]);
    setSuggestions([]);

    try {
      const response = await axios.post('http://localhost:8000/botman', { message: text });
      const botReply = response.data.messages?.[0]?.text || "🤖 Sorry, I didn't get that.";
      setMessages(prev => [...prev, { from: 'bot', text: botReply }]);
    } catch (error) {
      setMessages(prev => [...prev, { from: 'bot', text: "⚠️ Oops! Something went wrong." }]);
    }

    setUserInput('');
  };

  const handleSuggestionClick = (suggestion) => {
    sendMessage(suggestion);
  };

  const quickOptions = ['Reset Password', 'Check Email', 'Check Order', 'Make Payment'];

  return (
    <div className="chatbot-wrapper">
      <button className="chat-toggle" onClick={() => setVisible(!visible)}>
        💬 Chat
      </button>

      {visible && (
        <div className="chatbox">
          <div className="chat-header">Virtual Assistant</div>

          <div className="chat-messages">
            {messages.map((msg, index) => (
              <div key={index} className={`message ${msg.from}`}>
                {msg.text}
              </div>
            ))}
          </div>

          {suggestions.length > 0 && (
            <ul className="suggestions-list">
              {suggestions.map((sug, index) => (
                <li key={index} onClick={() => handleSuggestionClick(sug)}>
                  {sug}
                </li>
              ))}
            </ul>
          )}

          <div className="quick-buttons">
            {quickOptions.map((option, i) => (
              <button key={i} onClick={() => sendMessage(option)}>{option}</button>
            ))}
          </div>

          <div className="chat-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={() => sendMessage()}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Chatbot;
