// src/components/ChatBot.js

import React, { useState } from 'react';
import axios from 'axios';

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');

  const sendMessage = async () => {
    if (!userInput) return;

    const userMessage = { sender: 'You', text: userInput };
    setMessages([...messages, userMessage]);

    try {
      const response = await axios.post('http://localhost:8000/botman', {
        driver: 'web',
        userId: '123',
        message: userInput
      });

      const botReply = { sender: 'Bot', text: response.data };
      setMessages(prev => [...prev, botReply]);
      setUserInput('');
    } catch (err) {
      console.error('Bot error:', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Customer Support Chatbot</h2>
      <div style={{ height: '300px', overflowY: 'scroll', border: '1px solid #ccc', padding: '10px' }}>
        {messages.map((msg, idx) => (
          <div key={idx}><strong>{msg.sender}:</strong> {msg.text}</div>
        ))}
      </div>
      <input
        type="text"
        value={userInput}
        onChange={e => setUserInput(e.target.value)}
        placeholder="Type your message..."
        style={{ width: '80%', marginTop: '10px' }}
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default ChatBot;
